@Injectable({
    providedIn: 'root'
})
export class StatusMsgService {

    constructor(private _http: HttpClient,
        @Inject(KuiCoreConfigToken) public config) {
    }

getStatusMsg(): Observable<any> {

    return this._http.get(this.config.app + '/assets/i18n/statusMsg.json')
        .pipe(map(
            (res: any) => {
                return res;
            },
            err => {
                console.error(err);
            }
        )
        );

}
}
